#include <iostream>#include <iomanip>using namespace std;struct DaThuc{	float a[100];	int n;};typedef struct DaThuc DATHUC;void Nhap(DATHUC& f){	cout << "Nhap bac da thuc: ";}void Xuat(DATHUC f){	cout << setw(6);	cout << setprecision(3);	for (int i = f.n; i >= 1; i--)	{		cout << f.a[i];		cout << "x^" << i;	}	cout << f.a[0];}int main(){	DATHUC dt;	Nhap(dt);	Xuat(dt);	return 0;}